import React,{useState} from 'react'
import axios from 'axios';
export default function Category() {

    const api = "http://localhost:8080/admin/api/category";

    const [cate,setCate] = useState({id:null,name:''});

    const create = ()=>{
        axios.put(api+"/create",cate)
        .then(res => setCate(res.data))
        .catch(error => console.log(error));
    }

    const update = ()=>{
        axios.post(api+"/update",cate)
        .then(res => setCate(res.data))
        .catch(error => console.log(error));
    }

    const clean = ()=>{
        setCate({id:null,name:''});
    }

    const change = (e)=>{
        let target = e.target;
        setCate({...cate,[target.name]:target.value});
    }

    return (
            <div className="mt-5">
      <form className = "row d-flex justify-content-center mt-3 mb-3">
        <div className="form-group mb-4">
          <label htmlFor="formname">name</label>
          <input
            type="text"
            className="form-control"
            id="formname"
            name = "name"
            onChange = {change}
            value = {cate.name}
            placeholder="enter name"
          />
        </div>
        {cate.id!=null && <button type="button" className="btn btn-primary ml-3" onClick={update}>
          Update
        </button>}
       
    
        <button type="button" className="btn btn-info ml-3" onClick={clean}>Clean</button>
        {
            cate.id==null && <button  type="button" className="btn btn-success ml-3" onClick={create}>
          Create
        </button>
        }
        
        
        
      </form>
    </div>
    )
}
